<?phpfunction removeAcentos($string){	$string = ereg_replace("[АЮБЦ╙]","a",$string);	$string = ereg_replace("[аюбц]","A",$string);	$string = ereg_replace("[ИХЙ]","e",$string);	$string = ereg_replace("[ихй]","E",$string);	$string = ereg_replace("[МЛ]","i",$string);	$string = ereg_replace("[мл]","I",$string);	$string = ereg_replace("[СРТУ╨]","o",$string);	$string = ereg_replace("[срту]","O",$string);	$string = ereg_replace("[ЗЫШ]","u",$string);	$string = ereg_replace("[зыш]","U",$string);	$string = ereg_replace("Г","c",$string);	$string = ereg_replace("г","C",$string);	$string = ereg_replace("[][><}{)(:;,!?*%~^`&#@]","",$string);	$string = ereg_replace(" ","_",$string);	return $string;}?>