<?phpfunction removeAcentos($string){	$string = ereg_replace("[����]","a",$string);	$string = ereg_replace("[����]","A",$string);	$string = ereg_replace("[���]","e",$string);	$string = ereg_replace("[���]","E",$string);	$string = ereg_replace("[��]","i",$string);	$string = ereg_replace("[��]","I",$string);	$string = ereg_replace("[����]","o",$string);	$string = ereg_replace("[����]","O",$string);	$string = ereg_replace("[���]","u",$string);	$string = ereg_replace("[���]","U",$string);	$string = ereg_replace("�","c",$string);	$string = ereg_replace("�","C",$string);	$string = ereg_replace("[][><}{)(:;,!?*%~^`&#@]","",$string);	$string = ereg_replace(" ","_",$string);	return $string;}?>